import 'package:firebase_database/firebase_database.dart';import 'package:flutter/material.dart';import 'package:work_app/extensions/dropdown_input.dart';import 'package:work_app/extensions/text_input.dart';import 'package:work_app/views/students_page.dart';class HomePage extends StatefulWidget {  const HomePage({super.key});  @override  State<HomePage> createState() => _HomePageState();}class _HomePageState extends State<HomePage> {  DatabaseReference dbstd = FirebaseDatabase.instance.ref();  final _formKey = GlobalKey<FormState>();  final TextEditingController number = TextEditingController();  final TextEditingController name = TextEditingController();  String couValue = '';  String empValue = '';  String speValue = '';  @override  Widget build(BuildContext context) {    return Directionality(        textDirection: TextDirection.rtl,        child: Scaffold(          appBar: AppBar(            leading: IconButton(              onPressed: () {                Navigator.push(                  context,                  MaterialPageRoute(builder: (context) => const StudentsPage()),                );              },              icon: const Icon(Icons.group),            ),            elevation: 0,          ),          body: Container(            child: Column(              children: [                const Center(                    child: Text(                  'سجل أستلام كشوف \n   الطلبة الوافدين',                  style: TextStyle(fontWeight: FontWeight.bold, fontSize: 25),                )),                Expanded(                  child: SingleChildScrollView(                    child: Container(                      height: MediaQuery.sizeOf(context).height * 0.75,                      padding: const EdgeInsets.symmetric(horizontal: 12.0),                      child: Form(                        key: _formKey,                        child: Column(                          mainAxisAlignment: MainAxisAlignment.spaceEvenly,                          children: [                            TextInput(                              hintName: 'تسلسل: ',                              isNumber: true,                              controller: number,                            ),                            TextInput(                              hintName: 'أسم الطالب: ',                              controller: name,                            ),                            DropdownInput(                              isCou: true,                              onChanged: (value) {                                couValue = value!;                              },                            ),                            DropdownInput(                              onChanged: (value) {                                speValue = value!;                              },                            ),                            DropdownInput(                                isEmp: true,                                onChanged: (value) {                                  empValue = value!;                                }),                            Center(                              child: ElevatedButton.icon(                                onPressed: () {                                  if (_formKey.currentState!.validate()) {                                    ScaffoldMessenger.of(context).showSnackBar(                                      const SnackBar(                                          content: Text('Processing Data')),                                    );                                  }                                },                                icon: const Icon(Icons.add),                                label: const Text('اضافة طالب'),                              ),                            )                          ],                        ),                      ),                    ),                  ),                ),              ],            ),          ),        ));  }}